mapper.select:
select * from (
select * from (
    select
        rownum as rn
        , b.*
    from ((select * from board order by num desc) b)
) where rn >= #{startNum}
) where rn <= #{endNum}

mapper.update:
update board
	set readcount = readcount+1
where num = #{param}

mapper.insert:
	insert into reply (
        num
        , boardnum
        , userid
        , content
	) values (
        reply_seq.nextval
        , #{boardnum}
        , #{userid}
        , #{content}
	)

mapper.update:
	update board 
    set
        pass = #{pass}
        , email = #{email}
        , title = #{title}
        , content = #{content}
	where num = #{num}

mapper.insert:
insert into board (
    num
    , userid
    , pass
    , email
    , title
    , content
    , image
) values (
    board_seq.nextval
    , #{userid}
    , #{pass}
    , #{email}
    , #{title}
    , #{content}
    , #{image}
)
